mapper.insert:
	INSERT INTO MEMBER(   MEMBERID
   					     , PASSWD 
   					     , EMAIL
   					  )
                 VALUES(    #{memberId} 
                  	      , #{passwd}
                 		  , #{email})

mapper.select:
SELECT MEMBERID, PASSWD
FROM MEMBER
WHERE MEMBERID = #{memberId}
AND PASSWD = #{passwd}

SELECT COUNT(MEMBERID)
FROM MEMBER
WHERE MEMBERID = #{memberId}
