mapper.insert:
	INSERT INTO member(   memberID
   					     , PASSWD 
   					     , EMAIL
   					  )
                 VALUES(    #{memberId} 
                  	      , #{passwd}
                 		  , #{email})

mapper.select:
SELECT memberID, EMAIL, USERTYPE, REGDATE
FROM member
WHERE memberID = #{memberId}
AND PASSWD = #{passwd}

SELECT COUNT(memberID)
FROM member
WHERE memberID = #{memberId}

SELECT MEMBERID
FROM MEMBER
WHERE EMAIL = #{email}

SELECT PASSWD
FROM MEMBER
WHERE EMAIL = #{email}
AND MEMBERID = #{memberId}
