mapper.select:
SELECT MEMBERID, PASSWD, EMAIL, USERTYPE, REGDATE, ACTIVE
FROM MEMBER
WHERE MEMBERID = #{ memberId }

mapper.update:
UPDATE MEMBER
SET EMAIL = #{ email }, PASSWD = #{ passwd }, active = #{ active }
WHERE MEMBERID = #{ memberId }

UPDATE MEMBER
SET ACTIVE = false
WHERE MEMBERID = #{ memberId }
