mapper.update:
UPDATE BOARD_FREE
SET FREE_READCOUNT = FREE_READCOUNT + 1
WHERE BOARD_FREE_NO = #{ board_free_no }

UPDATE BOARD_FREE
SET FREE_DELETED = true
WHERE BOARD_FREE_NO = #{ board_free_no }

UPDATE BOARD_FREE
SET FREE_TITLE = #{ free_title }, FREE_CONTENT = #{ free_content }
WHERE BOARD_FREE_NO = #{ board_free_no }
